

const readlineSync = require('readline-sync');
var firstNumber = readlineSync.questionInt("Please enter your first number: ");
var secondNumber = readlineSync.questionInt("Please enter your second number: ");
var enteredOperator = readlineSync.question("Please enter the operation to perform: add, sub, mul, div ");

//Adding two numbers
function addTwoNumbers(firstNumber, secondNumber){
    return firstNumber + secondNumber;
}

//Subtracting two numbers
function subtractTwoNumbers(firstNumber, secondNumber){
    return firstNumber - secondNumber;
}

//Multiplying two numbers
function multiplyTwoNumbers(firstNumber, secondNumber){
    return firstNumber * secondNumber;
}

//Dividing two numbers
function divideTwoNumbers(firstNumber, secondNumber){
    return firstNumber / secondNumber;
}
    
//function definitions add, sub, mul, div of two numbers
function myCalculator(firstNumber, secondNumber, enteredOperator){
    if (enteredOperator == "add"){
        console.log("When You Add the First Number: " + firstNumber + " with the Second Number: " + secondNumber +  " it gives you a result of: " + addTwoNumbers(firstNumber, secondNumber));
    }
    else if(enteredOperator == "sub"){
        console.log("When You Subtract the First Number: " + firstNumber + " with the Second Number: " + secondNumber +  " it gives you a result of: " + subtractTwoNumbers(firstNumber, secondNumber));
    }
    else if(enteredOperator == "mul"){
        console.log("When You Multiply the First Number: " + firstNumber + " with the Second Number: " + secondNumber +  " it gives you a result of: " + multiplyTwoNumbers(firstNumber, secondNumber));
    }
    else if(enteredOperator == "div"){
        console.log("When You Divide the First Number: " + firstNumber + " with the Second Number: " + secondNumber +  " it gives you a result of: " + divideTwoNumbers(firstNumber, secondNumber));
    }

}
myCalculator(firstNumber, secondNumber, enteredOperator);